  return (
    <div
      data-slot="alert"
      role="alert"
      className={cn(
        alertVariants({ variant, size, icon, appearance }),
        className,
      )}
      {...props}
    >
      {children}
      {close && (
        <button
          onClick={onClose}
          aria-label="Dismiss"
          data-slot="alert-close"
          className={cn("group size-4 shrink-0 cursor-pointer")}
        >
          <X className="size-4 opacity-60 group-hover:opacity-100" />
        </button>
      )}
    </div>
  );
}

